import React, { Component } from "react";

const data = [{ mealID: 1, date: "01-22-2024", isDinner:false, day: "Monday", description: "Grilled Cheese", spotstaken: 0 }, 
    { mealID: 2, date: "01-23-2024", isDinner:false, day: "Tuesday", description: "BLT", spotstaken: 0 }, 
    { mealID: 3, date: "01-24-2024", isDinner:false, day: "Wedensday", description: "Chicken Salad", spotstaken: 0 }, 
    { mealID: 4, date: "01-25-2024", isDinner:false, day: "Thursday", description: "Tomato Soup", spotstaken: 0 }, 
    { mealID: 5, date: "01-22-2024", isDinner:true, day: "Monday", description: "Steak", spotstaken: 0 }, 
    { mealID: 6, date: "01-23-2024", isDinner:true, day: "Tuesday", description: "Salmon", spotstaken: 0 }, 
    { mealID: 7, date: "01-24-2024", isDinner:true, day: "Wedensday", description: "Chicken Alfredo", spotstaken: 0 }, 
    { mealID: 8, date: "01-25-2024", isDinner:true, day: "Thursday", description: "Spaghetti", spotstaken: 0 }];

const MenuItem = ({ item }) => {
  return (
    <div className="flex flex-col items-center justify-center bg-white p-4 rounded-md shadow-md w-96 mt-4">
      <h2 className="text-xl font-semibold mb-2">{item.day}</h2>
      <p className="text-gray-500 mb-4">{item.date}</p>
      <ul className="text-gray-600">
        <li>{item.description}</li>
      </ul>
    </div>
  );
};

const Menu = () => {
  return (
    <div className="flex flex-col items-center justify-center min-h-screen py-2">
      <h1 className="text-3xl font-bold">Menu</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        {data.map((item) => (
          <MenuItem key={item.mealID} item={item} />
        ))}
      </div>
    </div>
  );
};
export default Menu;
