import React, { Component } from "react";

const MenuItem = ({ item }) => {
  return (
    <div className="flex flex-col items-center justify-center bg-white p-4 rounded-md shadow-md w-96">
      <h2 className="text-xl font-semibold mb-2">{item.title}</h2>
      <p className="text-gray-500 mb-4">{item.days}</p>
      <ul className="text-gray-600">
        {item.items.map((i) => (
          <li>{i}</li>
        ))}
      </ul>
    </div>
  );
};

const Menu = () => {
  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-50">
      <h1 className="text-3xl font-semibold mb-4">Menu</h1>
      <div className="flex flex-col items-center justify-center bg-white p-4 rounded-md shadow-md w-96">
        <h2 className="text-xl font-semibold mb-2">Lunch</h2>
        <p className="text-gray-500 mb-4">Monday - Friday</p>
        <ul className="text-gray-600">
          <li>Grilled Cheese</li>
          <li>BLT</li>
          <li>Chicken Salad</li>
          <li>Tomato Soup</li>
        </ul>
      </div>
      <div className="flex flex-col items-center justify-center bg-white p-4 rounded-md shadow-md w-96 mt-4">
        <h2 className="text-xl font-semibold mb-2">Dinner</h2>
        <p className="text-gray-500 mb-4">Monday - Sunday</p>
        <ul className="text-gray-600">
          <li>Steak</li>
          <li>Salmon</li>
          <li>Chicken Alfredo</li>
          <li>Spaghetti</li>
        </ul>
      </div>
    </div>
  );
};
export default Menu;
