import React, {useState} from 'react';

const AddMeal = () => {
    const [meal, setMeal] = useState({
        date: "",
        description: "",
        isDinner: true,
    });

    const handleChange = (e) => {
        // console.log(e.target.name, e.target.value);
        if (e.target.name === "isDinner") {
            setMeal({...meal, [e.target.name]: !e.target.checked});
        } else {
            setMeal({ ...meal, [e.target.name]: e.target.value });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:3001/api/addMeal", {
            headers: {
                "content-type": "application/json",

            }
        })
    };

    return (
        <div>
        <form onSubmit={handleSubmit}>
            <input
            type="date"
            name="date"
            value={meal.date}
            onChange={handleChange}
            />  
            <input
            type="text"
            name="description"
            placeholder="description"
            value={meal.description}
            onChange={handleChange}
            />
            <label>Is this a dinner meal? </label>
            <input
            type="checkbox"
            name="isDinner"
            value={meal.isDinner}
            onChange={handleChange}
            />


            <button className="ml-4 border bg-green-500 text-white py-1 px-1 rounded" type="submit">Add Meal</button>
        </form>
        </div>
    );
    };

export default AddMeal;
