import React from 'react'
import { useValidation } from '../utils/ValidationContext'; // Import the useValidation hook
import { useNavigate } from 'react-router-dom';



const Login = () => {

    const {validated, setValidatedWithExpiration} = useValidation();
    console.log("protected routes " + validated);
    useEffect(() => {
      if (validated) {
        // 
        navigate('/home');
      } else {
        // Redirect to backend for authentication
        window.location.href = 'http://localhost:3001/auth/google';
        setValidatedWithExpiration(true); // Update validation state after successful authentication
      }
    }, [validated, navigate, setValidatedWithExpiration]);
  return (
    <div>login</div>
  )
}

export default Login;
