import React, { UseEffect, useState } from 'react';
import {BannerScreen} from "../components/BannerScreen";


const NameForm = () => {
    const [inputs, setInputs] = useState({});
    const [success, setSuccess] = useState([]);
    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch("http://localhost:3001/api/setUser", {
            headers: {
                "Content-Type": "application/json",
            },
            method: "POST",
            credentials: 'include',
            body: JSON.stringify(inputs),
        })
        .then(res => {
            if (res.ok) {
                return res.json();
            } else {
                throw new Error('Network response was not ok');
            }
        })
        .then(data => {
            if (data.sucess) {
                setSuccess(true);
            } else {
                setSuccess(false)
            }
        })
        .catch(error => {
            console.log("fetch error:", error);
        });
    }
    return (
        <form className="w-full max-w-xs">
            <div className="mb-4">
                <label for="firstName" className="block mb-2 text-sm font-medium text-gray-900 dark:text-black" >Enter Your First Name:</label>
                <input 
                    type="text"
                    name="firstName" 
                    className="hadow-sm bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500 dark:shadow-sm-light"
                    value={inputs.firstName || ""} 
                    onChange={handleChange}
                    required
                />
            </div>


            <label>Do you have a full meal plan (Most live ins):
                <select name="mealPlan" onChange={handleChange}>
                    <option value="yes">Yes</option>
                    <option value="no" selected>No</option>
                </select>
            </label>
            <input type="submit"/>
            {success
            ?   <div class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
                    <strong class="font-bold">Failure!</strong>
                    <span class="block sm:inline">Something went wrong when setting your name</span>
                    <span class="absolute top-0 bottom-0 right-0 px-4 py-3">
                        <svg class="fill-current h-6 w-6 text-red-500" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><title>Close</title><path d="M14.348 14.849a1.2 1.2 0 0 1-1.697 0L10 11.819l-2.651 3.029a1.2 1.2 0 1 1-1.697-1.697l2.758-3.15-2.759-3.152a1.2 1.2 0 1 1 1.697-1.697L10 8.183l2.651-3.031a1.2 1.2 0 1 1 1.697 1.697l-2.758 3.152 2.758 3.15a1.2 1.2 0 0 1 0 1.698z"/></svg>
                    </span>
                </div> 
            :   <div class="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative" role="alert">
                    <strong class="font-bold">Success!</strong>
                    <span class="block sm:inline">Something went wrong when setting your name</span>
                    <span class="absolute top-0 bottom-0 right-0 px-4 py-3">
                        <svg class="fill-current h-6 w-6 text-red-500" role="button" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><title>Close</title><path d="M14.348 14.849a1.2 1.2 0 0 1-1.697 0L10 11.819l-2.651 3.029a1.2 1.2 0 1 1-1.697-1.697l2.758-3.15-2.759-3.152a1.2 1.2 0 1 1 1.697-1.697L10 8.183l2.651-3.031a1.2 1.2 0 1 1 1.697 1.697l-2.758 3.152 2.758 3.15a1.2 1.2 0 0 1 0 1.698z"/></svg>
                    </span>
                </div>
          }
            </form>

    )
}
export default NameForm;
