import React, {useEffect, useState} from 'react'
import { Outlet, Navigate } from "react-router-dom";
import { useValidation } from './ValidationContext'; // Import the useValidation hook

function ProtectedRoutes() {
    // const {validated} = useValidation();
    // console.log("protected routes " + validated);
    // // Handle loading state if validation is still being checked

    // if (validated === null) {
    //     return <div>Loading...</div>; // replace with loading ui
    // }
    const checkBackendValidation = async () => {
        try {
          const res = await fetch('http://localhost:3001/api/validation');
          if (!res.ok) {
            throw new Error(`HTTP error, Status: ${res.status}`);
          }
          const data = await res.json();
          if (data.isAuthenticated) {
            setValidatedWithExpiration(true);
          } else {
            localStorage.removeItem('validated');
            setValidated(false);
          }
        } catch (err) {
          console.error('Error fetching validation data:', err);
          setValidated(false);
        }
      };

    return (
        <div>
            {validated ? <Outlet/> : <Navigate to="/login"/>}
        </div>
    )
}
export default ProtectedRoutes
